namespace Jinja2;internal class JinjaVisitor : JinjaParserBaseVisitor<List<IBlock>>{    public override List<IBlock> VisitTemplate(JinjaParser.TemplateContext context)    {        var blocks = new List<IBlock>();        var visitor = new ExpressionVisitor();        var statements = context.children;        foreach (var statement in statements)             blocks.AddRange(visitor.Visit(statement));                return blocks;    }    public override List<IBlock> VisitMacro_template(JinjaParser.Macro_templateContext context)    {        var blocks = new List<IBlock>();                var visitor = new ExpressionVisitor();        var statements = context.children;        foreach (var statement in statements)             blocks.AddRange(visitor.Visit(statement));                return blocks;   }    public override List<IBlock> VisitIf_template(JinjaParser.If_templateContext context)    {        var blocks = new List<IBlock>();                var visitor = new ExpressionVisitor();        var statements = context.children;        foreach (var statement in statements)             blocks.AddRange(visitor.Visit(statement));                return blocks;    }    public override List<IBlock> VisitFor_template(JinjaParser.For_templateContext context)    {        var blocks = new List<IBlock>();        var visitor = new ExpressionVisitor();        var statements = context.children;        foreach (var statement in statements)            blocks.AddRange(visitor.Visit(statement));        return blocks;    }}